/**
 * Determines whether value is defined?
 */
export declare function isDef<V>(val: V): val is NonNullable<V>;
/**
 * Determines whether value is Function type?
 */
export declare function isFunc(val: unknown): val is Function;
/**
 * Determines whether value is string type
 */
export declare function isString(val: unknown): val is string;
/**
 * Determines whether value is number type?
 * @param val any value type
 * @param strict whether we should ignore `NaN` from number type? default: false
 */
export declare function isNumber(val: unknown, strict?: boolean): val is number;
/**
 * Determines whether value is strict number(exclude NaN)?
 */
export declare function isStrictNumber(val: unknown): val is number;
/**
 * Determines whether an object has a property with the specified name.
 */
export declare function hasOwnProp<V extends object>(val: V, key: string | number | symbol): key is keyof V;
